    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        double S;
        double s1 = 0;
        double s2 = 0;
        double[] x = new double[4];
        double[][] a = new double[4][4];
        for (int k = 0; k < 4; k++){
            x[k] = Math.random() * (10 - (-10)) + (-10);
            jTable2.setValueAt(x[k],0,k);
            s1+=x[k]*Math.tan(x[k]);
        }
        for (int i = 0; i < 4; i++){
            for (int j = 0; j < 4; j++){
                a[i][j] = Math.random()*(10-(-10))+(-10);
                jTable1.setValueAt(a[i][j],j,i+1);
                s2+=Math.sqrt(Math.abs(Math.sin(a[i][j])));
            }
        }
        S=s2/s1;
        jTextField1.setText(String.valueOf(S));
    }//GEN-LAST:event_jButton1ActionPerformed

