    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        double[] x = new double[4];
        double[] a = new double[4];
        double[][] b = new double[4][4];
        double s1=0;
        double s2=0;
        
         for (int i=0; i<4; i++){
                x[i]=Math.random()*(10+10)-10;
                jTable4.setValueAt(x[i], 0, i);
                for(int j=0; j<4; j++){
                   b[i][j]=Math.random()*(10+10)-10; 
                   jTable1.setValueAt(b[i][j], j, i+1);
                }
                
            }
        
        for(int k=0; k<4; k++){
            
            for (int i=0; i<4; i++){
                s1+=x[i]*b[i][k]*b[i][k];
                for(int j=0; j<4; j++){
                    s2+=b[i][j]*Math.pow(Math.E, -b[i][j]);
                }
                
            }
            a[k]=(x[k]*s1)/Math.log(Math.abs(s2));
            jTable2.setValueAt(a[k], 0, k);
        }
        
    }//GEN-LAST:event_jButton1ActionPerformed

